#define TASK_STACK_SIZE	(1024U)
#define SCHED_STACK_SIZE	(1024U)

#define SRAM_BASE	(0x20000000U)
#define SRAM_SIZE	(128U * 1024U)
#define SRAM_END	(SRAM_BASE + SRAM_SIZE)

