// systick timer
#define SYSTICK_HZ	(16000000)
#define TICK_HZ	(1U) // 1KHZ
#define SYST_CSR_BASE	(0xE000E010)
#define SYST_RVR_BASE	(0xE000E014)

uint32_t *task1_stack;
uint32_t *task2_stack;
uint32_t *task3_stack;
uint32_t *task4_stack;
